property.value:
//MethodDeclaration[MethodDeclarator[
@Image='onAttach' or
@Image='onCreate' or
@Image='onCreateView' or
@Image='onActivityCreated' or
@Image='onConfigurationChanged' or
@Image='onPostCreate' or
@Image='onPostResume' or
@Image='onRestart' or
@Image='onRestoreInstanceState' or
